// actually O(n^3) but the cache of sum handled some special test case well...
class SplitArrayEqualSumDp {
    Map<Integer, int[][]> map = new HashMap<>();
    int[] right;

    public boolean splitArray(int[] a) {
        int n = a.length;
        int sum = 0;
        right = new int[n];
        right[n - 1] = a[n - 1];
        for (int i = n - 2; i >= 0; i--) {
            right[i] = right[i + 1] + a[i];
        }
        for (int i = 0; i < n; i++) {
            sum += a[i];
            int[][] dp = map.get(sum);
            if (dp == null) {
                dp = new int[n][3];
                map.put(sum, dp);
            }
            boolean can = dos(a, i + 2, sum, 2, dp);
            if (can) {
                return true;
            }
        }
        return false;
    }

    boolean dos(int[] a, int i, int sum, int times, int[][] dp) {
        int n = a.length;
        if (times < 0) {
            return false;
        }
        if (i >= n) {
            return false;
        }
        if (times == 0) {
            return right[i] == sum;
        }
        if (dp[i][times] != 0) {
            return dp[i][times] == 1;
        }
        int csum = 0;
        for (int j = i; j < n; j++) {
            csum += a[j];
            if (csum == sum) {
                boolean can = dos(a, j + 2, sum, times - 1, dp);
                if (can) {
                    dp[i][times] = 1;
                    return true;
                }
            }
        }
        dp[i][times] = 2;

        return false;
    }
}
