import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/*
LC#30
You are given a string, s, and a list of words, words, that are all of the same length. Find all starting indices of substring(s) in s that is a concatenation of each word in words exactly once and without any intervening characters.

Example 1:

Input:
  s = "barfoothefoobarman",
  words = ["foo","bar"]
Output: [0,9]
Explanation: Substrings starting at index 0 and 9 are "barfoor" and "foobar" respectively.
The output order does not matter, returning [9,0] is fine too.
Example 2:

Input:
  s = "wordgoodgoodgoodbestword",
  words = ["word","good","best","word"]
Output: []
 */

// the wordlen is fixed so each substring is actually a sliding window, time is ripe for a sliding window solution...
// difficulty of finding which word is dropped is mitigated by a loop on wordlen
// wordlen*slen in time complexity
public class SubstringWithConcationOfAllWords {

    public List<Integer> findSubstring(String s, String[] words) {
        List<Integer> r = new ArrayList<>();
        if (words.length == 0) {
            return r;
        }
        Map<String, Integer> map = new HashMap<>();
        for (String w : words) {
            map.put(w, map.getOrDefault(w, 0) + 1);
        }
        int wlen = words[0].length();

        // if wlen =3 and we start at 3 it's the same as start at 0
        for (int t = 0; t < wlen; t++) {
            int start = t;
            Map<String, Integer> cur = new HashMap<>();
            for (int i = t; i + wlen - 1 < s.length(); i += wlen) {
                String sub = s.substring(i, i + wlen);
                cur.put(sub, cur.getOrDefault(sub, 0) + 1);
                int mapcount = map.getOrDefault(sub, 0);
                // keep ing moving start when > its fair share
                while (cur.getOrDefault(sub, 0) > mapcount) {
                    String toremove = s.substring(start, start + wlen);
                    int nc = cur.getOrDefault(toremove, 0) - 1;
                    putToMap(cur, toremove, nc);
                    start += wlen;
                }
                if (cur.equals(map)) {
                    r.add(start);
                }
            }
        }
        return r;
    }

    private void putToMap(Map<String, Integer> cur, String toput, int nc) {
        if (nc == 0) {
            cur.remove(toput);
        } else {
            cur.put(toput, nc);
        }
    }

    public static void main(String[] args) {
        //       String s = "";
        //    String[] words = {"ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba", "ab", "ba"};
        String s = "";
        String[] words = {};

        System.out.println(new SubstringWithConcationOfAllWords().findSubstring(s, words));
    }
}
